document.querySelectorAll(".ajax-form").forEach((form=>{form.addEventListener("submit",(function(e){e.preventDefault();const formData=new FormData(this),urlsearchparams=new URLSearchParams(formData);fetch(this.action,{method:this.method||"POST",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:urlsearchparams.toString()}).then((response=>{response.text().then((data=>{response.ok?alert(data):alert("Error at send form. Status"+response.status+". Message:"+data)}))})).catch((error=>{alert(error)}))}))}));