document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll(".ajax-form").forEach((form=>{form.addEventListener("submit",(function(e){e.preventDefault();const formData=new FormData(this),urlsearchparams=new URLSearchParams(formData);fetch(this.action,{method:this.method||"POST",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:urlsearchparams.toString()}).then((response=>{if(!response.ok)throw new Error(response.status+" "+response.statusText);response.text()})).then((data=>{alert(data)})).catch((error=>{alert(error)}))}))}));const headerContainer=document.getElementById("ajax-header");headerContainer?fetch("header.min.html").then((response=>{if(!response.ok)throw new Error("Fetch: Cannot load header");return response.text()})).then((data=>{headerContainer.innerHTML=data;const currentPath=window.location.pathname.split("/").pop();document.querySelectorAll(".navegation a").forEach((link=>{link.getAttribute("href").split("/").pop()!==currentPath||link.classList.add("active")}))})).catch((error=>console.error(error))):console.error("No element with class 'ajax-header' found in the document.")}));